    private ArrayList<Pessoa> pessoas;

    public void geraPessoas(){
        ArrayList<Pessoa> pessoas = new ArrayList<>();
        Random rand = new Random();
        for(int i = 0; i < 1; i++){
            pessoas.add(new PessoaBemInformada(rand.nextInt(28)+1, rand.nextInt(58)+1, 56, String.valueOf(i+100)));
        }
        setPessoas(pessoas);
    }

    private void setPessoas(ArrayList<Pessoa> pessoas) {
        this.pessoas = pessoas;
    }

    public int[][][] desenhaPessoas(ArrayList<Pessoa> pessoas) {
        int[][][] mapa = new int[0][][];
        for (Pessoa p : pessoas) {
            mapa = getMundo();
            mapa[p.getX()][p.getY()][0] = p.getCor();
        }
        return mapa;
    }

    private int[][][] getMundo() {
        return mundo;
    }

    public void desenhaMundo(){
        int[][][] mapa = desenhaPessoas(this.pessoas);
        for(int i = 0; i < 30; i++){
            for(int j = 0; j < 60; j++){
                int len = mapa[i][j].length;
                if(mapa[i][j][len-1] != 0){
                    System.out.print("â–ˆ");
                }else{
                    System.out.print(" ");
                }
            }
            System.out.println();
        }
        //downcasting para usar o metodo move() da classe PessoaBemInformada
        for(Pessoa p : pessoas){
            ((PessoaBemInformada)p).move();
        }

    }
